function mouse_body2(x,y,a,b,cx,cy,c1,lw)% mouse_body: function to plot a mouse body given x,y position and a,b% length and width%% form: mouse_body(x,y,a,b,cx,cy,c1)% % x,y are the x and y coordinate for the centroid of the ellipse% a,b are the major and minor axis diameters of the ellipse% cx,cy are the length of the x and y lines of the center cross% c1 is the color and linestyle for the whole plot; accepts rgb or letter% string (i.e., 'r')% lw is linewidth%%phi=linspace(pi,2*pi,40);x_semicircle=(cos(phi)*b)+x;y_center_semicircle=(y-(a-b));y_semicircle=(sin(phi)*b)+y_center_semicircle;x_left_line=[-b 0]+x;y_left_line=[0 (2*a)-b]+y_center_semicircle;x_right_line=[0 b]+x;y_right_line=[(2*a)-b 0]+y_center_semicircle;plot(x_semicircle,y_semicircle,'color',c1,'linewidth',lw);hold onplot(x_left_line,y_left_line,'color',c1,'linewidth',lw);plot(x_right_line,y_right_line,'color',c1,'linewidth',lw);% plot([x-cx x+cx],[(ya+b) (ya+b)],'color',c1);plot([x-cx,x+cx],[y,y],'color',c1,'linewidth',lw);% plot([x x],[(ya+b)-cy (ya+b)+cy],'color',c1);plot([x,x],[y-cy,y+cy],'color',c1,'linewidth',lw);% axis equalend